mapper.select:
select 

from QUARTZ_DEPLOY 
where 1=1 

    AND ID = #{id} 


    AND NAME = #{name} 


    AND CREATETIME = #{createtime} 


    AND UPDATETIME = #{updatetime} 


    AND BIGENTIME = #{bigentime} 


    AND STATUS = #{status} 


    AND REMARK = #{remark} 


    AND CRON = #{cron} 


    AND CRON = #{cron}

 	select count(*) from QUARTZ_DEPLOY	where 1=1

	 			AND name = #{name}


	 			AND  cron = #{cron}

	 select c.*
  		from (select G.*, rownum rn

          from (select g.* from QUARTZ_DEPLOY g
           where 1=1

		 			AND g.name = #{name}


		 			AND  g.cron = #{cron}

            order by #{order}) G ) c	
	 	where c.rn   >=  #{pageNumber} and c.rn   <=  #{pageSize}
